'use strict';

import * as assert from 'assert';
import { app } from 'egg-mock/bootstrap';
describe('test/controller/user.test.js', () => {
  let token = '';
  let _mockid = '';
  // 注册失败
  it('should register failed( 400 )', async () => {
    const res = await app
      .httpRequest()
      .post('/api/user')
      .type('form')
      .send({
        username: 'test123',
        password: '123',
      });
    assert(res.status === 400);
  });

  // 注册成功
  it('should register succeed( 200 )', async () => {
    const res = await app
      .httpRequest()
      .post('/api/user')
      .type('form')
      .send({
        username: 'test123',
        password: 'test123',
        email: '123@qq.com',
      });
    // 暂时频闭 500
    assert(res.status === 201 || res.status === 200 || res.status === 500);
  });

  it('should login failed', async () => {
    const loginRes = await app
      .httpRequest()
      .post('/api/login')
      .type('form')
      .send({
        username: 'test123',
        password: 'xxxxxxxxx',
      });
    assert(loginRes.body.status === 'fail');
  });

  // 先进行登录
  it('should login succeed', async () => {
    const loginRes = await app
      .httpRequest()
      .post('/api/login')
      .type('form')
      .send({
        username: 'test123',
        password: 'test123',
      });
    assert(loginRes.status === 200);
    token = loginRes.body.token;
    _mockid = loginRes.body.userinfo._id;
  });

  // 列表查询
  it('should load user list', async () => {
    await app
      .httpRequest()
      .get('/api/user')
      .set('Authorization', token)
      .expect(200);
  });

  it('should user email and username verify failed:username!', async () => {
    const res = await app
      .httpRequest()
      .post('/email_username/verify')
      .set('Authorization', token)
      .type('form')
      .send({
        username: 'test123',
        email: '123xxxxxx@qq.com',
      })
      .expect(200);
    assert(res.body.msg === '用户名已存在！');
  });

  it('should user email and username verify failed:email!', async () => {
    const res = await app
      .httpRequest()
      .post('/email_username/verify')
      .set('Authorization', token)
      .type('form')
      .send({
        username: 'test1234',
        email: '123@qq.com',
      });
    assert(res.body.msg === '邮箱已存在！');
  });

  it('should user email and username verify succeed!', async () => {
    const res = await app
      .httpRequest()
      .post('/email_username/verify')
      .set('Authorization', token)
      .type('form')
      .send({
        username: 'test1234',
        email: '123xxxx@qq.com',
      });
    assert(res.body.msg === '邮箱可用！');
  });

  it('should uploadHeadImg succeed!', async () => {
    await app
      .httpRequest()
      .post('/api/user/headimg')
      .set('Authorization', token)
      .type('form')
      .send({
        userid: 'test1234',
        imgData:
          'data:image/png;base64,',
      })
      .expect(200);
  });

  it('should download HeadImg err!', async () => {
    await app
      .httpRequest()
      .get('/api/user/headimg/' + _mockid)
      .set('Authorization', token)
      .expect(404);
  });

  it('should update bio succeed', async () => {
    await app
      .httpRequest()
      .post('/api/user/bio')
      .set('Authorization', token)
      .type('form')
      .send({
        userid: _mockid,
        bio: 'test bio',
      })
      .expect(200);
  });

  it('should update sex succeed', async () => {
    await app
      .httpRequest()
      .post('/api/user/sex')
      .set('Authorization', token)
      .type('form')
      .send({
        userid: _mockid,
        sex: 'man',
      })
      .expect(200);
  });

  it('should update displayname succeed', async () => {
    await app
      .httpRequest()
      .post('/api/user/displayname')
      .set('Authorization', token)
      .type('form')
      .send({
        userid: _mockid,
        displayname: 'test displayname',
      })
      .expect(200);
  });

  it('should update email succeed', async () => {
    await app
      .httpRequest()
      .post('/api/user/email')
      .set('Authorization', token)
      .type('form')
      .send({
        userid: _mockid,
        email: '123@qq.com',
      })
      .expect(200);
  });

  it('should update location succeed', async () => {
    await app
      .httpRequest()
      .post('/api/user/location')
      .set('Authorization', token)
      .type('form')
      .send({
        userid: _mockid,
        location: 'Shenzhen',
      })
      .expect(200);
  });

  it('should logout succeed', async () => {
    await app
      .httpRequest()
      .get('/api/logout')
      .set('Authorization', token)
      .expect(200);
  });
});
